import axios from "axios";
import { useEffect, useState } from "react";
import { TouchableOpacity, View, Text } from "react-native";
import { useRoute, useNavigation } from "@react-navigation/native";

const UpdateOffer = ({id}) => {
const [job, setOffer] = useState({ position: "", languages: [], salary: 0 });
const [dirty, setDirty] = useState(false);

useEffect(() => {
axios
.get("http://172.29.38.90:8000/api/offer/" + id)
.then((res) => setOffer(res.data))
.catch((err) => console.log(err));
}, [id]);

useEffect(() => {
if (dirty) {
OfferHandler();
setDirty(false);
}
}, [dirty]);

const handleCheckboxChange = (value) => {
setOffer((prevState) => {
const newLanguages = [...prevState.languages];
if (newLanguages.includes(value)) {
const index = newLanguages.indexOf(value);
if (index !== -1) {
newLanguages.splice(index, 1);
}
} else {
newLanguages.push(value);
}
setDirty(true);
return { ...prevState, languages: newLanguages };
});
};

const OfferHandler = () => {
console.log(job);
axios
.put("http://172.29.38.90:8000/api/offer/${id}, job")
.then((res) => {
console.log(res);
})
.catch((err) => console.log(err));
};

return (
<View>
<Text>Actualización de Oferta:</Text>
<TouchableOpacity onPress={() => navigation.navigate('Offers')}>
<Text>Offer</Text>
</TouchableOpacity>
<View>
<Text>Job: {job.position}</Text>
<Text>Salary: {job.salary}</Text>
<View>
<Text>Lenguajes Requeridos:</Text>
<TouchableOpacity onPress={() => handleCheckboxChange("JS")}>
<Text>{job.languages.includes("JS") ? "✅" : "❌"} JS</Text>
</TouchableOpacity>
<TouchableOpacity onPress={() => handleCheckboxChange("CSS")}>
<Text>{job.languages.includes("CSS") ? "✅" : "❌"} CSS</Text>
</TouchableOpacity>
</View>
</View>
</View>
);
};

export default UpdateOffer;
